select

from user u

    1 = 1


            AND u.`gender` = #{param.gender, jdbcType=CHAR}


            AND STR_TO_DATE(CONCAT_WS('-', u.`year`, u.`month`, u.`date`), '%Y-%m-%d %H:%M:%s') <=
            #{param.minAge, jdbcType=TIMESTAMP}


            AND STR_TO_DATE(CONCAT_WS('-', u.`year`, u.`month`, u.`date`), '%Y-%m-%d %H:%M:%s') >=
            #{param.maxAge, jdbcType=TIMESTAMP}


            AND u.`constellation` = #{param.constellation, jdbcType=VARCHAR}


            AND u.`id` NOT IN (

                #{uid}

            )



ORDER BY u.`id` ASC

SELECT

FROM `user` ur
WHERE ur.`gender` = #{param.gender, jdbcType=CHAR}
AND ur.`id` != #{param.userId, jdbcType=BIGINT}
AND str_to_date(concat_ws('-', ur.`year`, ur.`month`, ur.`date`), '%Y-%m-%d %H:%M:%s') <= #{param.minAge, jdbcType=TIMESTAMP}
AND str_to_date(concat_ws('-', ur.`year`, ur.`month`, ur.`date`), '%Y-%m-%d %H:%M:%s') >= #{param.maxAge, jdbcType=TIMESTAMP}
ORDER BY ur.`create_time` DESC, ur.`update_time` DESC, ur.`id` DESC
