mapper.select:
		select

		from user u

			1 = 1


					AND u.`gender` = #{param.gender, jdbcType=CHAR}


					AND STR_TO_DATE(CONCAT_WS('-', u.`year`, u.`month`, u.`date`), '%Y-%m-%d %H:%M:%s') <= #{param.minAge, jdbcType=TIMESTAMP}


					AND STR_TO_DATE(CONCAT_WS('-', u.`year`, u.`month`, u.`date`), '%Y-%m-%d %H:%M:%s') >= #{param.maxAge, jdbcType=TIMESTAMP}


					AND u.`constellation` = #{param.constellation, jdbcType=VARCHAR}


					AND u.`id` NOT IN (

						#{uid}

					)



        ORDER BY u.`id` ASC
